function [S]=DB0(x1,y1,a,b,D)
B0=[(y1/b - 1)/(4*a),                  0,   -(y1/b - 1)/(4*a),                   0,   (y1/b + 1)/(4*a),                  0,   -(y1/b + 1)/(4*a),                   0
                  0, (x1/(4*a) - 1/4)/b,                   0, -(x1/(4*a) + 1/4)/b,                  0, (x1/(4*a) + 1/4)/b,                   0, -(x1/(4*a) - 1/4)/b
 (x1/(4*a) - 1/4)/b,   (y1/b - 1)/(4*a), -(x1/(4*a) + 1/4)/b,   -(y1/b - 1)/(4*a), (x1/(4*a) + 1/4)/b,   (y1/b + 1)/(4*a), -(x1/(4*a) - 1/4)/b,   -(y1/b + 1)/(4*a)];
S=D*B0; 